import React, { useState } from 'react';

const Post = () => {

  // State for follow/unfollow
  const [isFollowing, setIsFollowing] = useState(false);

  // State for show more/less
  const [isExpanded, setIsExpanded] = useState(false);

  // State for image carousel
  const images = [
    'data:image/jpeg;base64,', // Replace with actual image URLs
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
  ];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // State for likes
  const [likes, setLikes] = useState(23);
  const [isLiked, setIsLiked] = useState(false);

  // State for comments
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  // Toggle follow/unfollow
  const handleFollowToggle = () => {
    setIsFollowing(!isFollowing);
  };

  // Toggle show more/less
  const handleToggleContent = () => {
    setIsExpanded(!isExpanded);
  };

  // Navigate images
  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Handle like/unlike
  const handleLikeToggle = () => {
    setLikes(isLiked ? likes - 1 : likes + 1);
    setIsLiked(!isLiked);
  };

  // Handle comment submission
  const handleCommentSubmit = (e) => {
    e.preventDefault();
    if (newComment.trim()) {
      setComments([...comments, newComment]);
      setNewComment('');
    }
  };

  // Post content (long text for testing show more/less)
  const postContent = "احتفلنا مع فريق الأنمي اليوم بمرور أربعة أعوام من أجمل الأوقات التي قضيناها معاً، حيث أن الأنمي عالمٌ من الخيال والإبداع، عالمٌ من الإثارة والتشويق، عالمٌ مليء بالمغامرات والأبطال الذين لا يُنسَون.ي قضيناها معاً، حيث أن الأنمي عالمٌ من الخيال والإبداع، عالمٌ من الإثارة والتشويق، عالمٌ مليء بالمغامرات والأبطال الذين لا يُنسَون.ي قضيناها معاً، حيث أن الأنمي عالمٌ من الخيال والإبداع، عالمٌ من الإثارة والتشويق، عالمٌ مليء بالمغامرات والأبطال الذين لا يُنسَون.";

  return (
    <div dir="rtl" className="bg-white rounded-xl shadow-lg p-4 mb-4 max-w-[100%] mx-auto">
      {/* Club Info and Follow Button */}
      <div className="flex justify-between items-center mb-2">
        <div className="flex items-center">
          <img
            src="data:image/jpeg;base64," // Replace with actual club profile image
            alt="Club Profile"
            className="w-10 h-10 rounded-full mr-2"
          />
          <div>
            <h3 className="text-base font-semibold text-gray-800">نادي الأنمي المستقبل</h3>
            <p className="text-xs text-gray-500">22:00 - 04/21/2025</p>
          </div>
        </div>
        <button
          onClick={handleFollowToggle}
          className={`px-3 py-1 rounded-full text-sm font-medium ${
            isFollowing ? 'bg-gray-200 text-gray-700' : 'bg-blue-600 text-white'
          }`}
        >
          {isFollowing ? 'إلغاء المتابعة' : 'متابعة'}
        </button>
      </div>

      {/* Post Content with Show More/Less */}
      <div className="mb-3">
        <p className={`text-sm text-gray-700 ${isExpanded ? '' : 'line-clamp-2'}`}>
          {postContent}
        </p>
        <button
          onClick={handleToggleContent}
          className="text-blue-600 text-sm font-medium mt-1"
        >
          {isExpanded ? 'عرض أقل' : 'عرض المزيد'}
        </button>
      </div>

      {/* Image Carousel */}
      <div className="relative mb-3">
        <img
          src={images[currentImageIndex]}
          alt={`Post Image ${currentImageIndex + 1}`}
          className="w-full h-48 object-cover rounded-lg"
        />
        {/* Navigation Buttons */}
        {images.length > 1 && (
          <>
            <button
              onClick={handlePrevImage}
              className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-800 bg-opacity-50 text-white p-2 rounded-full"
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M15 19l-7-7 7-7"
                />
              </svg>
            </button>
            <button
              onClick={handleNextImage}
              className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-800 bg-opacity-50 text-white p-2 rounded-full"
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M9 5l7 7-7 7"
                />
              </svg>
            </button>
          </>
        )}
      </div>

      {/* Like and Comment Buttons */}
      <div className="flex justify-between items-center mb-3">
        <button
          onClick={handleLikeToggle}
          className="flex items-center text-blue-600"
        >
          <svg
            className={`w-5 h-5 mr-1 ${isLiked ? 'fill-current' : 'fill-none'}`}
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              stroke="currentColor"
              strokeWidth="2"
              d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"
            />
          </svg>
          <span className="text-sm">{likes}</span>
        </button>
        <button className="flex items-center text-blue-600">
          <svg
            className="w-5 h-5 mr-1"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M8 12h10M8 6h10M8 18h10M4 4h16v16H4z"
            />
          </svg>
          <span className="text-sm">{comments.length}</span>
        </button>
      </div>

      {/* Comment Section */}
      <div className="border-t pt-2">
        <form onSubmit={handleCommentSubmit} className="flex items-center mb-2">
          <input
            type="text"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="أضف تعليقاً..."
            className="w-full p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 h-10"
            dir="rtl"
          />
          <button
            type="submit"
            className="mr-4 bg-blue-600 text-white px-3 py-1 rounded-lg h-10"
          >
            إرسال
          </button>
        </form>
        {/* Display Comments */}
        {comments.map((comment, index) => (
          <div key={index} className="flex items-start mb-2">
            <img
              src="https://via.placeholder.com/30?text=User" // Replace with user profile image
              alt="User Profile"
              className="w-8 h-8 rounded-full mr-2"
            />
            <div className="bg-gray-100 p-2 rounded-lg">
              <p className="text-sm text-gray-700">{comment}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Post;